import axios from 'axios';
import { login_user } from '../constant/userConstant';



export const userLogin = async (mydata)=>{
    const config = {
        headers: { "Content-Type": "application/json" },
      };
    const data = await axios.post('/api/v1/login',mydata,config).then(
        alert.success("login successfull")).catch((error)=>{
          alert.error(error);
          console.log(error);
        });
    return{
        type: login_user,
        payload: data
    };
};
