import axios from 'axios';
import {api_request,success_true,success_fail} from '../constant/contacConstant';


export const getContact = ()=>
async(dispatch)=>{
    try {
        dispatch({type: api_request})
        const data = axios.get('/api/v1/contact')
        dispatch({type:success_true, payload:data})
    } catch (error) {
        dispatch({
            type: success_fail,
            payload: error.response.data.message,
          });

    }

}

export const postContact = (data)=>
async(dispatch)=>{
    try {
        dispatch({type: api_request})
        const config = {
            headers: { "Content-Type": "application/json" },
          };

          const { data } = await axios.post(
            `/api/v1/post/contact`,
            data,
            config
          );


        dispatch({type:success_true, payload:data})
    } catch (error) {
        dispatch({
            type: success_fail,
            payload: error.response.data.message,
          });

    }

}
