import { makeStyles } from '@mui/styles';
import React, { useState } from 'react'
import { useSelector } from 'react-redux';
import { ProcuctCard } from './ProcuctCard';

const useStyle = makeStyles((theme) => ({
    gardent01: {
        background: "linear-gradient(29deg, rgba(2,0,36,1) 24%, rgba(69,4,88,1) 71%, rgba(62,57,184,1) 100%)"
      },
      flexCenter: {
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        flexWrap:'wrap'
    },
    flexCenterColumn: {
        display: "flex",
        justifyContent: "center",
        // alignItems: "center",
        flexDirection: "column",
    },
}));



const Products = () => {
    const classes = useStyle();
  const { product } = useSelector((state) => state.product);

  return (
    <>
    <div
      className={`${classes.gardent01} ${classes.flexCenter}`}
      style={{ width: "100vw", minHeight: "100vh" }}
    >
       {product && product.map((item,index)=>(
        <ProcuctCard product={product} index={index} />
       ))}
    </div>
    </>
  )
}

export default Products
