import {get_contact} from '../constant/contacConstant';


export const contactReducer = (state={allQuries:[]},{type,payload,loading})=>{
        switch (type) {
            case get_contact:
                return{
                    ...state,
                    allQuries: payload,
                    loading:loading   
                }
            default:
                return state;
        }
}
