import axios from "axios";
import { userLoginAction } from "../actions/userAction";



export const LoginUserpost =(mydata) => async (dispatch) => {
  const config = {
    headers: { "Content-Type": "application/json" },
  };
  const req = await axios.post("/api/v1/login", mydata, config).then(
    console.log("login successfull")).catch((error)=>{
      alert.error(error);
      console.log(error);
    });

    const data = req.data.token
    dispatch(userLoginAction(data));

};
