import axios from "axios";
import { Navigate } from "react-router-dom";
import Cookie from "universal-cookie";
import { userLoginAction } from "../actions/userAction";

const cookie = new Cookie();

export const LoginUserpost = (mydata) => async (dispatch) => {
  const config = {
    headers: {
      "Content-Type": "application/json",
      "Access-Control-Allow-Origin": "*",
    },
  };
  const req = await axios
    .post("https://btisoft.herokuapp.com/api/v1/login", mydata, config)
    .then(console.log("login successfull"))
    .catch((error) => {
      console.log(error);
    });
  if (req.data.success === true) {
    window.alert("login succesful");
    window.location.reload();
  }

  const data = { user: req.data.user, token: req.data.token };
  window.localStorage.setItem("token", req.data.token);
  // cookie.set("token",req.data.token , {path: "/", expires: new Date(new Date().getTime() + 86400 * 1000  )});
  dispatch(userLoginAction(data));
};
