import { makeStyles } from '@mui/styles';
import axios from 'axios';
import React, { useEffect } from 'react'
import { Navigate } from 'react-router-dom';
import DashSideBar from './DashSideBar';

const useStyle = makeStyles((theme)=>({
    flex_center:{
      display:'flex',
      justifyContent:'center',
      alignItems:'center',
    },
    flex_column:{
      display:'flex',
      justifyContent:'center',
      alignItems:'center',
      flexDirection:'column'
    },
    rox_box:{
      margin:'0.1vmax'
    },
    row:{
      border:'1px solid black',
      padding:'1vmax'
    }
  }))

const AdminUpdateProduct = ({id}) => {


    useEffect(()=>{
      const product = axios.get(`/api/v1/product/:id`)
    },[])

    console.log(product);

    const classes = useStyle();

    const token = window.localStorage.getItem('token')
    if(!token){
      return <Navigate replace to="/admin/login" />;

    }
    return (
      <div style={{display:'flex', position:"fixed", width:"100vw", zIndex:"10000", background:"white"}}>
      <DashSideBar />
        <div className={classes.flex_center} style={{width:'100%', height:'100vh'}}>
        <div  className={`${classes.flex_center}`}>
            <div className={classes.row}><img/></div>
            <div className={`${classes.row}${classes.flex_column}`}>
                <div className={`${classes.row}${classes.flex_center}`}>
                    <div>name: <input type='text' placeholder='name'/></div>
                    <div>catagory: <input type='text' placeholder='catagory'/></div>
                    <div>price: <input type='text' placeholder='price'/></div>
                </div>
                <div className={`${classes.row}${classes.flex_center}`}>
                  description:
                  <textarea/>
                </div>
            </div>
        </div>
        </div>


      </div>
    )
}

export default AdminUpdateProduct
