import axios from 'axios';
import { login_user } from '../constant/userConstant';



export const userLogin =  (mydata)=>{
    const config = {
        headers: { "Content-Type": "application/json" },
      };
    const data = axios.post('/api/v1/login',mydata,config).then((res)=>res.data)
    .then((data)=>{
        return data
    })
      console.log(data);
    return{
        type: login_user,
        payload: data
    };
};
