import { Button } from "@mui/material";
import { makeStyles } from "@mui/styles";
import React, { useState } from "react";
import DashSideBar from "./DashSideBar";

const useStyle = makeStyles((theme) => ({
  flex_center: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  flex_column: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexDirection:'column'
  },
  container: {
    width: "100vw",
    height: "100vh",
  },
  input:{
    padding:'1vmax',
    margin:'1vmax',
  }

}));

const Login = () => {
  const [email, setEmail] = useState();
  const [password, setPassword] = useState();

  const submitLogin = (e) => {
    e.preventDefault();
  };
  const classes = useStyle();

  return (
        <div style={{display:'flex', position:"fixed", width:"100vw", zIndex:"10000", background:"white"}}>
    <div className={`${classes.flex_center} ${classes.container}`}>
        <form onSubmit={submitLogin} className={classes.flex_column}>
          <input className={classes.input} type="email" value={email} placeholder="email" />
          <input className={classes.input} type="password" value={password} placeholder="password" />
          <Button sx={{fontSize:'3vmax'}} color='secondary' type="submit">Login</Button>
        </form>
      </div>


    </div>

  );
};

export default Login;
