import axios from "axios";
import { getOneProduct, getProduct } from "../actions/productAction";


export const fetchProduct = async (dispatch) => {
  const req = await axios.get("/api/v1/products").catch((error) => {
    console.log(error);
  });
  const data = req.data.products;
  dispatch(getProduct(data));
};

export const fetchOneProduct = async (dispatch) => {
  const req = axios.get(`/api/v1/product/62143f53acf78c4997565b01 `).catch((error) => {
    console.log(error);
  });
  const data = req.data;
  dispatch(getOneProduct(data));
};
