import {api_request,success_true,success_fail} from '../constant/contacConstant';

export const productReducer = ( state = { product:[]}, action )=>{

    switch (action.type) {
        case api_request:
            return{
                ...state,
                loading:true
            }
        case success_true:
            return{
                loading:false,
                products: action.payload.product
            }
        case success_fail:
            return {
                loading: false,
                error: action.payload,
              };
        default:
           return state;
    }

}
