import { Button } from '@mui/material';
import { makeStyles } from '@mui/styles';
import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { Navigate } from 'react-router-dom';
import { fetchOneProduct } from '../../redux/reduxAxois/fetchProduct';
import DashSideBar from './DashSideBar';

const useStyle = makeStyles((theme)=>({
    flex_center:{
      display:'flex',
      justifyContent:'center',
      alignItems:'center',
    },
    flex_column:{
      display:'flex',
      justifyContent:'center',
      alignItems:'center',
      flexDirection:'column'
    },
    rox_box:{
      margin:'0.1vmax'
    },
    row:{
      border:'1px solid black',
      padding:'1vmax'
    }
  }))

const AdminUpdateProduct = ({id}) => {

    const dispatch = useDispatch();
    const {product} = useSelector((state)=>state.onProduct);
    const {images} = product
    console.log(images);

    const [name, setName] = useState();
    const [description, setDescription] = useState();
    const [price, setPrice] = useState();
    const [category, setCategory] = useState();

    useEffect(()=>{
      // const product = axios.get(`/api/v1/product/ `)
      fetchOneProduct('62143f53acf78c4997565b01')(dispatch);
      // console.log(product);
    },[dispatch])

    const putreq = async ()=>{
      const config = {
        Headers:{'Content-Type':'application/json'}
      }
      const req = await axios.put('',config)
    }

    const classes = useStyle();

    const token = window.localStorage.getItem('token')
    if(!token){
      return <Navigate replace to="/admin/login" />;

    }
    return (
      <div style={{display:'flex', position:"fixed", width:"100vw", zIndex:"10000", background:"white"}}>
      <DashSideBar />
        <div className={classes.flex_center} style={{width:'100%', height:'100vh'}}>
        <div  className={`${classes.flex_center}`}>
            <div className={classes.row}>
            {images && images.map((item,index)=>(
              <img src={images[index].url}
            style={{width:'10vmax'}} key={index}/>
            ))}
            </div>
            <div className={`${classes.row}${classes.flex_column}`}>
                <div className={`${classes.row}${classes.flex_center}`}>
                    <div>name: <input type='text' value={product.name} placeholder='name'
                      onChange={(e)=>{setName(e.target.value)}}
                    /></div>
                    <div>category: <input type='text' value={product.category} placeholder='category'
                      onChange={(e)=>{setCategory(e.target.value)}}
                    /></div>
                    <div>price: <input type='text' value={product.price} placeholder='price'
                      onChange={(e)=>{setPrice(e.target.value)}}
                    /></div>
                </div>
                <div className={`${classes.flex_center}`}>
                  description:
                  <textarea  value={product.description} 
                    onChange={(e)=>{setDescription(e.target.value)}}
                  />
                  <Button onClick={putreq} >update</Button>
                </div>
            </div>
        </div>
        </div>


      </div>
    )
}

export default AdminUpdateProduct
