import { Button } from "@mui/material";
import { makeStyles } from "@mui/styles";
import React, { useState } from "react";
import { useAlert } from "react-alert";
import { useDispatch } from "react-redux";
import { userLogin } from "../../redux/actions/userAction";
import DashSideBar from "./DashSideBar";

const useStyle = makeStyles((theme) => ({
  flex_center: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  flex_column: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexDirection:'column'
  },
  container: {
    width: "100vw",
    height: "100vh",
  },
  input:{
    padding:'1vmax',
    margin:'1vmax',
  }

}));

const Login = () => {

  const dispatch = useDispatch()

  const [email, setEmail] = useState();
  const [password, setPassword] = useState();
  const alert = useAlert();




  const submitLogin = (e) => {
    e.preventDefault();
    const myform = new FormData();
    myform.set('email',email);
    myform.set('password',password);
    console.log(myform);
    dispatch(userLogin(myform)).then(
      alert.success("login successfull")).catch((error)=>{
        alert.error(error);
        console.log(error);
      });
  };
  const classes = useStyle();

  return (
        <div style={{display:'flex', position:"fixed", width:"100vw", zIndex:"10000", background:"white"}}>
    <div className={`${classes.flex_center} ${classes.container}`}>
        <form onSubmit={submitLogin} className={classes.flex_column}>
          <input className={classes.input} type="email" value={email} placeholder="email"
          onChange={(e)=>{setEmail(e.target.value)}}
           />
          <input className={classes.input} type="password" value={password} placeholder="password" 
            onChange={(e)=>{setPassword(e.target.value)}}
          />
          <Button sx={{fontSize:'1.5vmax'}} color='secondary' type="submit">Login</Button>
        </form>
      </div>


    </div>

  );
};

export default Login;
