import { container, Paper, Typography, useMediaQuery } from "@mui/material";
import { makeStyles } from "@mui/styles";
import { Box } from "@mui/system";
import React from "react";
import apiimg from "../../img/api.jpg";
import codelaptop from "../../img/code-laptop.jpg";
import codesample from "../../img/code-sample.webp";
import cursorp from "../../img/circle-cursor-hover.png";

const useStyle = makeStyles((theme) => ({
  gardent: {
    background:
      "linear-gradient(29deg, rgba(2,0,36,1) 0%, rgba(27,0,202,1) 35%, rgba(57,156,184,1) 64%)",
  },
  sld01: {
    position: "fixed",
    zIndex:'-2',
    // display: 'block'
    // marginBottom:"100vh"
    top: "0vh",
    margin: "0px",
    padding: "0px",
  },
  flexCenter: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  },
  flexCenterSpacebetween: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
  },
  homecards: {
    display: "flex",
    justifyContent: "space-evenly",
    alignItems: "center",
    margin: "1vmax",
    padding: "vmax",
    [theme.breakpoints.down("sm")]: {
      flexDirection: "column",
    },
  },
  flexCenterColumn: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "column",
  },
  color: {
    color: theme.palette.primary.font,
  },
  bckcolor: {
    backgroundColor: theme.palette.primary.main,
  },
  p: {
    textAlign: "center",
    fontSize: "2vmax !important",
    [theme.breakpoints.down("md")]: {
      position: "absolute",
      top: "5vmax",
      padding: "4vmax",
    },
  },
  hover: {
    "&:hover": {
      // backgroundColor: "red"
    },
  },
}));

const Home = () => {
  const classes = useStyle();
  const mobile = useMediaQuery("(max-width:600px)");

  return (
    <div
      className={classes.flexCenterColumn}
      style={{ maxWidth: "100% !important" }}
    >
      <div
        className={`${classes.flexCenter} ${classes.gardent} ${classes.sld01} shadow `}
        style={{ width: "100%", height: "100vh", margin: "0px" }}
      >
        <Typography className={`${classes.color} ${classes.p} `}>
          BTIsoft is a better solution for all web and business software,
          <br /> we are a customer-oriented company,
          <br /> because we belives customers imagination is our work, <br />{" "}
          and that makes us different and precise for customer thinking,
          <br />
          we turn your thinking into reality.
        </Typography>
      </div>

      <div
        className={`${classes.flexCenterColumn} ${classes.bckcolor} ${classes.color}`}
        style={{ zIndex: "10", marginTop: "100vh" }}
      >
        <div className={`${classes.homecards}`}>
          <div>
            <img src={apiimg} alt="apiimg" width="500vmax" />
          </div>
          <div>
            <Typography sx={{ padding: "20px", margin: "20px" }}>
              Lorem ipsum dolor sit amet consectetur adipisicing elit.
              Cupiditate voluptatibus praesentium nihil natus explicabo dolores
              provident fuga in iusto veritatis, perspiciatis, sit at velit
              atque veniam unde et odio reprehenderit mollitia! Inventore
              reprehenderit nam illum quas, similique ipsa doloremque aliquam!
              Sint sequi recusandae velit nulla eveniet saepe quasi nam
              exercitationem.
            </Typography>
          </div>
        </div>
        <div className={`${classes.homecards}`}>
          {mobile ? (
            <div>
              {" "}
              <div>
                <img src={codelaptop} alt="codelaptop" width="500vmax" />
              </div>
              <div>
                <Typography sx={{ padding: "20px", margin: "20px" }}>
                  Lorem ipsum dolor sit amet consectetur adipisicing elit.
                  Cupiditate voluptatibus praesentium nihil natus explicabo
                  dolores provident fuga in iusto veritatis, perspiciatis, sit
                  at velit atque veniam unde et odio reprehenderit mollitia!
                  Inventore reprehenderit nam illum quas, similique ipsa
                  doloremque aliquam! Sint sequi recusandae velit nulla eveniet
                  saepe quasi nam exercitationem.
                </Typography>
              </div>
            </div>
          ) : (
            <div className={`${classes.homecards}`}>
              {" "}
              <div>
                <Typography sx={{ padding: "20px", margin: "20px" }}>
                  Lorem ipsum dolor sit amet consectetur adipisicing elit.
                  Cupiditate voluptatibus praesentium nihil natus explicabo
                  dolores provident fuga in iusto veritatis, perspiciatis, sit
                  at velit atque veniam unde et odio reprehenderit mollitia!
                  Inventore reprehenderit nam illum quas, similique ipsa
                  doloremque aliquam! Sint sequi recusandae velit nulla eveniet
                  saepe quasi nam exercitationem.
                </Typography>
              </div>
              <div>
                <img src={codelaptop} alt="codelaptop" width="500vmax" />
              </div>
            </div>
          )}
        </div>
        <div className={`${classes.homecards}`}>
          <div>
            <img src={codesample} alt="codesample" width="500vmax" />
          </div>
          <div>
            <Typography sx={{ padding: "20px", margin: "20px" }}>
              Lorem ipsum dolor sit amet consectetur adipisicing elit.
              Cupiditate voluptatibus praesentium nihil natus explicabo dolores
              provident fuga in iusto veritatis, perspiciatis, sit at velit
              atque veniam unde et odio reprehenderit mollitia! Inventore
              reprehenderit nam illum quas, similique ipsa doloremque aliquam!
              Sint sequi recusandae velit nulla eveniet saepe quasi nam
              exercitationem.
            </Typography>
          </div>
      <div
        className={`${classes.flexCenter} ${classes.gardent} shadow `}
        style={{ width: "100%", height: "100%", margin: "0px", zIndex:"20", alignSelf:"flex-end" }}
        >
        <Typography className={`${classes.color} ${classes.p} `}>
        <h4>Advice</h4>
        All projects start with an idea.<br /> We will help to transform that idea into the project's<br />requirement and strategy.<br /> We advise on choosing the correct technology <br />stack and an external services<br /> as well as set up the timeline of the project.
        </Typography>
      </div>
    </div>
        </div>
      </div>

  );
};

export default Home;
