import axios from "axios";
import { userLoginAction } from "../actions/userAction";



export const LoginUserpost =(mydata) => async (dispatch) => {
  const config = {
    headers: { "Content-Type": "application/json" },
  };
  const req = await axios.post("/api/v1/login", mydata, config).then(
    console.log("login successfull")).catch((error)=>{
      console.log(error);
    });

    if(req.data.success === true){
      alert.success('massage sent sucessfully')
    }

    const data = {user:req.data.user, token:req.data.token}
    window.localStorage.setItem('token', req.data.token);
    dispatch(userLoginAction(data));

};
