import {api_request,success_true,success_fail,CLEAR_ERRORS} from '../constant/contacConstant';


export const contactReducer = (state={allQuries:[]},action)=>{
    switch (action.type) {
        case api_request:
            return{
                ...state,
                loading: true
            }
        case success_true:
            return{
                loading:false,
                allQuries: action.payload
            }
        case success_fail:
            return{
                loading:false,
                error: action.payload
            }
            case CLEAR_ERRORS:
      return {
        ...state,
        error: null,
      };
        default:
            return state;
    }

}
