import { Button } from "@mui/material";
import { makeStyles } from "@mui/styles";
import axios from "axios";
import React, { useState } from "react";
import { useAlert } from "react-alert";
import { useDispatch } from "react-redux";
import DashSideBar from "./DashSideBar";

const useStyle = makeStyles((theme) => ({
  flex_center: {
    display: "flex",
    flexWrap: "wrap",
    justifyContent: "center",
    alignItems: "center",
  },
  flex_column: {
    display: "flex",
    flexWrap: "wrap",
    flexDirection: "column",
  },
  input: {
    padding: "1vmax",
    margin: "1vmax",
  },
  card: {
    padding: "2vmax",
    margin: "2vmax",
    border: "1px solid black",
    position: "relative",
  },
  deletIcon: {
    color: "red",
  },
}));

const AdminCreateProduct = () => {
  const alert = useAlert();
  const classes = useStyle();

  const dispatch = useDispatch();

  const [name, setName] = useState();
  const [description, setDescription] = useState();
  const [price, setPrice] = useState();
  const [category, setCategory] = useState();
  const [images, setImages] = useState([]);
  const [imagesPreview, setImagesPreview] = useState([]);

  const createProduct = async (data) => {
    const config = {
      headers: { "Content-Type": "application/json" },
    };
    const req = await axios.post("", data, config).catch((error) => {
      console.error(error);
    });
    if (req.data.success === true) {
      alert.success("product created");
    }
  };
  const createProductForm = (e) => {
    e.preventDefault();

    const myForm = new FormData();

    myForm.set("name", name);
    myForm.set("price", price);
    myForm.set("description", description);
    myForm.set("category", category);

    images.forEach((image) => {
      myForm.append("images", image);
    });

    createProduct(myForm);
  };

  const createProductImagesChange = (e) => {
    const files = Array.from(e.target.files);

    setImages([]);
    setImagesPreview([]);

    files.forEach((file) => {
      const reader = new FileReader();

      reader.onload = () => {
        if (reader.readyState === 2) {
          setImagesPreview((old) => [...old, reader.result]);
          setImages((old) => [...old, reader.result]);
        }
      };

      reader.readAsDataURL(file);
    });
  };

  return (
    <div
      style={{
        display: "flex",
        position: "fixed",
        width: "100vw",
        zIndex: "10000",
        background: "white",
      }}
    >
      <DashSideBar />
      <div
        className={classes.flex_center}
        style={{ width: "100%", height: "100vh" }}
      >
        <form
          onSubmit={createProductForm}
          encType="multipart/form-data"
          className={classes.flex_column}
        >
          <input
            value={name}
            placeholder="product name"
            className={classes.input}
            onChange={(e) => {
              setName(e.target.value);
            }}
          />
          <input
            value={description}
            placeholder="description"
            className={classes.input}
            onChange={(e) => {
              setDescription(e.target.value);
            }}
          />
          <input
            value={price}
            placeholder="price"
            className={classes.input}
            onChange={(e) => {
              setPrice(e.target.value);
            }}
          />
          <input
            value={category}
            placeholder="category"
            className={classes.input}
            onChange={(e) => {
              setCategory(e.target.value);
            }}
          />
          <input
            type="file"
            name="avatar"
            accept="image/*"
            onChange={createProductImagesChange}
            multiple
          />
          <div id="createProductFormImage">
            {imagesPreview.map((image, index) => (
              <img key={index} src={image} alt="Product Preview" />
            ))}
          </div>
          <Button color="secondary" type="submite">
            Create
          </Button>
        </form>
      </div>
    </div>
  );
};

export default AdminCreateProduct;
