import { Button } from '@mui/material';
import { makeStyles } from '@mui/styles';
import axios from 'axios';
import React, { useState } from 'react'
import { useAlert } from 'react-alert';
import { useDispatch } from 'react-redux';
import DashSideBar from './DashSideBar'

const useStyle = makeStyles((theme)=>({
  flex_center:{
    display:'flex', 
    flexWrap:'wrap',
    // justifyContent:'center',
    // alignItems:'flex-start'
  },
  card:{
    padding:'2vmax',
    margin:'2vmax',
    border: '1px solid black',
    position:'relative'
  },
  deletIcon:{
    color:'red',

  }

}));


const AdminCreateProduct = () => {
  const alert = useAlert();
  const classes = useStyle();

  const dispatch = useDispatch();

  const [name,setName] = useState()
  const [discription,setDiscription] = useState()
  const [price,setPrice] = useState()
  const [category,setCategory] = useState()


  const createProduct = async (data)=>{

    const config ={
      headers: { "Content-Type": "application/json" },
    }
    const req = await axios.post('',data,config).catch((error)=>{
      console.error(error);
    })
    if(req.data.success === true){
      alert.success('product created')
    }

  }
 const createProductForm = (e)=>{
   e.preventDefault();
 }

  return (
    <div style={{display:'flex', position:"fixed", width:"100vw", zIndex:"10000", background:"white"}}>
    <DashSideBar />
      <div className={classes.flex_center}>
        <form onSubmit={createProductForm}>
        <input value={name} placeholder='product name'/>
        <input value={discription} placeholder='discription'/>
        <input value={price} placeholder='price'/>
        <input value={category} placeholder='category'/>
        <Button color='secondary' type='submite'>Create</Button>
        </form>
      </div>

    </div>
  )
}

export default AdminCreateProduct
