package javaprogram;

public class unary {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=2;
		int b=3;
		boolean bool=true;
		System.out.println(a--); // a=a-1
		System.out.println(a);
		System.out.println(-(-b));
		System.out.println(!bool);
		System.out.println(!(b<a));

	}

}
